import React from 'react';
import useFetch from './useFetch';
import Card from "./Card";

const Home = () => {
    const [data: reviews, isPending, error] = useFetch("https://script.google.com/macros/s/AKfycbwQIFS93nLgjx43Ynil_R5J-5TKBEXL8FbMZXfCZq4sxYJP5R3JBf7i1zCI35s98qmK/exec");

    return (
        <div className="home">
            <div className="section">
                <h1>Top Reviews</h1>
                <div className="gallery">
                    {isPending && <div>Loading...</div>}
                    {error && <div>Error.</div>}
                    {data.map((item, i) => (
                        <div className="card" key={i}>
                            <img src={item.image} />
                            <div className="card-details">
                                <h2 className="gold">{item.title}</h2>
                                <h4 className="beige">{item.artist}</h4>
                                <p className="tan">reviewed by {item.author}</p>
                            </div>
                        </div>
                    ))}
                </div>
            </div>
            <hr />
        </div>
    );
}

export default Home;
