import React, { useEffect, useState } from 'react';

const Review = ({ title, artist, author, score, url, image, quote, text }) => {
    return (
        <div className="review">
            <div className="review-banner section">
                <img src={image} />
                <div className="review-info">
                    <h1 className="gold">{title}</h1>
                    <h2>{artist}</h2>
                    <h2 className="chia">{score}/10</h2>
                    <p className="tan">reviewed by {author}</p>
                </div>
            </div>

            <div className="review-content section">
                <span className="tan quote">"{quote}"</span>
                <p className="review-text">{text}</p>
            </div>
        </div>
    );
}

export default Review;
